
public class Ex03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		char[] ch = new char[] {'a', 'p', 'p', 'l', 'e'};
		for (i = 0; i < 5; i++)
		{
			System.out.println("ch["+i+"] ="+ch[i]);
		}
		System.out.println();
		for (i = 4; i >= 0; i--)
		{
			System.out.println("ch["+i+"] = "+ch[i]);
		}
	}

}
